import React, { useState } from 'react';
import LoginPage from './LoginPage';
import TruckConnect from './TruckConnect';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    // This function would be triggered when the "Join now" button is clicked.
    setIsLoggedIn(true);
  };

  return (
    <div>
      {isLoggedIn ? (
        // If the user has "joined," we display the TruckConnect component
        <TruckConnect />
      ) : (
        // Otherwise, we show the LoginPage component
        // Pass the handleLogin function as a prop to the LoginPage
        <LoginPage onJoinNowClick={handleLogin} />
      )}
    </div>
  );
};

export default App;
