import React from 'react';
import { Box, Card, CardContent, Typography, Button, LinearProgress } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import './TruckConnect.css'; // Custom CSS for additional styling

const TruckConnect = () => {
  return (
    <Box className="truck-connect-container" sx={{ bgcolor: 'background.default', color: 'text.primary' }}>
      <Box sx={{ my: 4, textAlign: 'center' }}>
        <Typography variant="h3" gutterBottom>Welcome to TruckConnect</Typography>
        <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2 }}>
          <Button variant="contained">Driving Mode</Button>
          <Button variant="contained">Communication</Button>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <SearchIcon color="action" />
            <input className="search-input" type="text" placeholder="Search..." />
          </Box>
        </Box>
      </Box>

      <Card sx={{ maxWidth: 345, bgcolor: 'background.paper', mb: 2 }}>
        <CardContent>
          <Typography variant="h5" component="div">Real-time Updates</Typography>
          <Typography sx={{ mb: 1.5 }} color="text.secondary">Stay informed about traffic and breaks</Typography>
          <LinearProgress variant="determinate" value={72} />
        </CardContent>
      </Card>

      {/* Repeat the Card component for each section of your dashboard */}
      <Card sx={{ maxWidth: 345, bgcolor: 'background.paper' }}>
        <CardContent>
          <Typography variant="h5" component="div">Trip Insights</Typography>
          <Typography sx={{ mb: 1.5 }} color="text.secondary">3 Breaks Left</Typography>
          {/* More items */}
        </CardContent>
      </Card>

      {/* Additional content and cards */}
    </Box>
  );
};

export default TruckConnect;
