import React from 'react';
import './TruckConnect.css'; 

const TruckConnect = () => {
  const insights = [
    { icon: 'üìç', text: '3 Breaks Left', subtext: 'Gas Stations' },
    { icon: 'üí¨', text: '1 Suggested Break', subtext: 'Community Chat', button: 'Connect' },
    { icon: '‚úâÔ∏è', text: 'New Messages', subtext: 'Rest Area', button: 'Check-in' },
    { icon: '‚è∞', text: 'Break Reminder', subtext: 'Important', button: 'Join' }
  ];

  return (
    <div className="truck-connect-container">
      <header className="truck-connect-header">
        <div className="header-top">
          <h1>Welcome to TruckConnect</h1>
          <div className="search-container">
            <input type="text" placeholder="Search..." />
          </div>
        </div>
        <div className="header-bottom">
          <button className="header-btn">Driving Mode</button>
          <button className="header-btn">Communication</button>
        </div>
      </header>

      <main className="main-content">
        <section className="real-time-updates">
          <h2>Real-time Updates</h2>
          <p>Stay informed about traffic and breaks</p>
          <div className="progress-bar-background">
            <div className="progress-bar-foreground" style={{ width: '72%' }}></div>
          </div>
        </section>

        <section className="trip-insights">
          {insights.map((insight, index) => (
            <div className="insight-card" key={index}>
              <span className="insight-icon">{insight.icon}</span>
              <div className="insight-texts">
                <p className="insight-main-text">{insight.text}</p>
                <p className="insight-subtext">{insight.subtext}</p>
              </div>
              {insight.button && <button className="insight-btn">{insight.button}</button>}
            </div>
          ))}
        </section>
      </main>
    </div>
  );
};

export default TruckConnect;
